import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext.jsx';
import { useCart } from '../../context/CartContext.jsx';
import { 
  ShoppingCart, 
  User, 
  Search, 
  Menu, 
  X, 
  LogOut, 
  Settings,
  Package,
  BarChart3,
  Users,
  Wallet
} from 'lucide-react';

const Header = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const { totalItems } = useCart();
  const navigate = useNavigate();
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const handleLogout = () => {
    logout();
    navigate('/');
    setIsProfileOpen(false);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/products?search=${encodeURIComponent(searchQuery.trim())}`);
      setSearchQuery('');
    }
  };

  const getProfileMenuItems = () => {
    const baseItems = [
      { icon: Settings, label: 'Profile Settings', path: '/profile' },
      { icon: Wallet, label: 'Wallet', path: '/wallet' }
    ];

    switch (user?.role) {
      case 'admin':
        return [
          { icon: BarChart3, label: 'Admin Dashboard', path: '/admin/dashboard' },
          { icon: Users, label: 'Manage Users', path: '/admin/users' },
          ...baseItems
        ];
      case 'vendor':
        return [
          { icon: BarChart3, label: 'Vendor Dashboard', path: '/vendor/dashboard' },
          { icon: Package, label: 'My Products', path: '/vendor/products' },
          { icon: Package, label: 'Orders', path: '/vendor/orders' },
          ...baseItems
        ];
      case 'customer':
        return [
          { icon: BarChart3, label: 'My Dashboard', path: '/customer/dashboard' },
          { icon: Package, label: 'My Orders', path: '/customer/orders' },
          ...baseItems
        ];
      default:
        return baseItems;
    }
  };

  return (
    <header className="bg-white shadow-lg border-b border-secondary-200 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <div className="flex items-center">
            <Link to="/" className="flex items-center space-x-2">
              <div className="w-8 h-8 bg-gradient-to-r from-primary-600 to-primary-700 rounded-lg flex items-center justify-center">
                <span className="text-white font-bold text-lg">S</span>
              </div>
              <span className="text-xl font-bold text-secondary-900">Shanki-Vendor</span>
            </Link>
          </div>

          {/* Search Bar */}
          <div className="hidden md:flex flex-1 max-w-lg mx-8">
            <form onSubmit={handleSearch} className="w-full">
              <div className="relative">
                <input
                  type="text"
                  placeholder="Search products..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-secondary-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                />
                <Search className="absolute left-3 top-2.5 h-5 w-5 text-secondary-400" />
              </div>
            </form>
          </div>

          {/* Navigation */}
          <div className="flex items-center space-x-4">
            {/* Desktop Navigation */}
            <nav className="hidden md:flex items-center space-x-6">
              <Link
                to="/products"
                className={`text-secondary-700 hover:text-primary-600 font-medium transition-colors ${
                  location.pathname === '/products' ? 'text-primary-600' : ''
                }`}
              >
                Products
              </Link>
              <Link
                to="/categories"
                className={`text-secondary-700 hover:text-primary-600 font-medium transition-colors ${
                  location.pathname === '/categories' ? 'text-primary-600' : ''
                }`}
              >
                Categories
              </Link>
              {!isAuthenticated && (
                <Link
                  to="/vendor/register"
                  className="text-secondary-700 hover:text-primary-600 font-medium transition-colors"
                >
                  Become a Vendor
                </Link>
              )}
            </nav>

            {/* Cart */}
            {isAuthenticated && user?.role === 'customer' && (
              <Link
                to="/cart"
                className="relative p-2 text-secondary-700 hover:text-primary-600 transition-colors"
              >
                <ShoppingCart className="h-6 w-6" />
                {totalItems > 0 && (
                  <span className="absolute -top-1 -right-1 bg-primary-600 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                    {totalItems}
                  </span>
                )}
              </Link>
            )}

            {/* User Menu */}
            {isAuthenticated ? (
              <div className="relative">
                <button
                  onClick={() => setIsProfileOpen(!isProfileOpen)}
                  className="flex items-center space-x-2 p-2 rounded-lg hover:bg-secondary-100 transition-colors"
                >
                  <div className="w-8 h-8 bg-primary-600 rounded-full flex items-center justify-center">
                    <span className="text-white text-sm font-medium">
                      {user?.name?.charAt(0).toUpperCase()}
                    </span>
                  </div>
                  <span className="hidden md:block text-secondary-700 font-medium">
                    {user?.name}
                  </span>
                </button>

                {/* Profile Dropdown */}
                {isProfileOpen && (
                  <div className="absolute right-0 mt-2 w-56 bg-white rounded-lg shadow-lg border border-secondary-200 py-2 z-50">
                    <div className="px-4 py-2 border-b border-secondary-200">
                      <p className="text-sm font-medium text-secondary-900">{user?.name}</p>
                      <p className="text-xs text-secondary-500">{user?.email}</p>
                      <span className={`inline-block mt-1 px-2 py-1 text-xs rounded-full ${
                        user?.role === 'admin' ? 'bg-red-100 text-red-800' :
                        user?.role === 'vendor' ? 'bg-blue-100 text-blue-800' :
                        'bg-green-100 text-green-800'
                      }`}>
                        {user?.role?.charAt(0).toUpperCase() + user?.role?.slice(1)}
                      </span>
                    </div>

                    {getProfileMenuItems().map((item, index) => (
                      <Link
                        key={index}
                        to={item.path}
                        onClick={() => setIsProfileOpen(false)}
                        className="flex items-center px-4 py-2 text-sm text-secondary-700 hover:bg-secondary-100 transition-colors"
                      >
                        <item.icon className="h-4 w-4 mr-3" />
                        {item.label}
                      </Link>
                    ))}

                    <div className="border-t border-secondary-200 mt-2 pt-2">
                      <button
                        onClick={handleLogout}
                        className="flex items-center w-full px-4 py-2 text-sm text-red-600 hover:bg-red-50 transition-colors"
                      >
                        <LogOut className="h-4 w-4 mr-3" />
                        Logout
                      </button>
                    </div>
                  </div>
                )}
              </div>
            ) : (
              <div className="flex items-center space-x-3">
                <Link
                  to="/login"
                  className="text-secondary-700 hover:text-primary-600 font-medium transition-colors"
                >
                  Login
                </Link>
                <Link
                  to="/register"
                  className="btn-primary"
                >
                  Sign Up
                </Link>
              </div>
            )}

            {/* Mobile Menu Button */}
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="md:hidden p-2 text-secondary-700 hover:text-primary-600 transition-colors"
            >
              {isMenuOpen ? <X className="h-6 w-6" /> : <Menu className="h-6 w-6" />}
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden border-t border-secondary-200 py-4">
            {/* Mobile Search */}
            <form onSubmit={handleSearch} className="mb-4">
              <div className="relative">
                <input
                  type="text"
                  placeholder="Search products..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-secondary-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                />
                <Search className="absolute left-3 top-2.5 h-5 w-5 text-secondary-400" />
              </div>
            </form>

            {/* Mobile Navigation */}
            <nav className="space-y-2">
              <Link
                to="/products"
                onClick={() => setIsMenuOpen(false)}
                className="block px-3 py-2 text-secondary-700 hover:text-primary-600 font-medium transition-colors"
              >
                Products
              </Link>
              <Link
                to="/categories"
                onClick={() => setIsMenuOpen(false)}
                className="block px-3 py-2 text-secondary-700 hover:text-primary-600 font-medium transition-colors"
              >
                Categories
              </Link>
              {!isAuthenticated && (
                <Link
                  to="/vendor/register"
                  onClick={() => setIsMenuOpen(false)}
                  className="block px-3 py-2 text-secondary-700 hover:text-primary-600 font-medium transition-colors"
                >
                  Become a Vendor
                </Link>
              )}
            </nav>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
